    private void LoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_LoginActionPerformed
        // Login Button
        Boolean flag = false;
        input_password = password.getText();
        input_username = username.getText();
        try {
            Connection con = condb.getCon();
            Statement stm = con.createStatement();
            ResultSet retrieve_val = stm.executeQuery("SELECT username,password FROM login");
            String dat_username = "", dat_password = "";
            while (retrieve_val.next()) {
                dat_username = retrieve_val.getString(1);
                dat_password = retrieve_val.getString(2);
            }
            con.close();
            String sql_query = "select `username` , `password` from login where Sno =" + Sno;
            if (dat_username.equals(input_username) && dat_password.equals(input_password)) {
                System.out.println("Login Successfully ");
                JOptionPane.showMessageDialog(null, "Login Successfully");
                home_page obj = new home_page();
                obj.setVisible(true);
                setVisible(false);
            } else {
                System.out.println("Username or Password not Mathced!");
                JOptionPane.showMessageDialog(null, "Username or password not matched!");
            }
        } catch (Exception e) {
            System.out.println(e);
        }

    }//GEN-LAST:event_LoginActionPerformed

