    private void create_accActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_create_accActionPerformed

        try {
            Connection con = condb.getCon();
            Statement stm = con.createStatement();
            ResultSet retrieve_val = stm.executeQuery("SELECT * FROM login");
            boolean flag = true;
            while (retrieve_val.next()) {

                if (retrieve_val.getString(1) == "" && retrieve_val.getString(2) == "" && retrieve_val.getString(3) == "") {
                    flag = false;
                }

            }
            con.close();
            if (flag == false) {
                create_account new_acc = new create_account();
                new_acc.setVisible(true);
                setVisible(false);

            } else {
                System.out.println("You already have an account");
                JOptionPane.showMessageDialog(null, "You already have an account!");
            }
        } catch (Exception e) {
            System.out.println(e);
        }
    }//GEN-LAST:event_create_accActionPerformed

